local D, C, L = unpack(select(2, ...)) if not C["duffed"]["errorfilter"] then return endlocal f, o = CreateFrame("Frame"), L["errors"]["noerror"]f:SetScript("OnEvent", function(self, event, error)	if C["duffed"]["filter"][error] then UIErrorsFrame:AddMessage(error, 1, 0 ,0) else o = error endend)SLASH_TUKUIERROR1 = "/error"function SlashCmdList.DUFFEDUIERROR() print(o) endUIErrorsFrame:UnregisterEvent("UI_ERROR_MESSAGE")f:RegisterEvent("UI_ERROR_MESSAGE")